mapper.select:
select *
from user
where userDelete=0

  and username like '%${username}%'


  and email = #{email,jdbcType=VARCHAR}


  and phone = #{phoneNumber,jdbcType=INTEGER}


  and permissionLevel = #{level,jdbcType=INTEGER}

mapper.update:
UPDATE user
SET userDelete=1
WHERE userID = #{userId,jdbcType=INTEGER}
